                      <Switch>
                            <Route exact path='/' >
                              <AllBooks getToken={getToken}/>
                            </Route>
                            <Route path='/logout' >
                                  <Logout getToken={getToken} setToken={setToken}/>
                            </Route>
          <Switch>
